import{_ as o,c as a,ac as t,o as i}from"./chunks/framework.CeO_JeQa.js";const n="/document/image/vscode_plugin.png",c="/document/image/vscode_plugin_logo.png",s="/document/image/vscode_plugin_start.png",p="/document/image/vscode_plugin_export.png",l="/document/image/vscode_plugin_target.png",r="/document/image/vscode_plugin_build.png",d="/document/image/vscode_plugin_file_tree.png",_="/document/image/vscode_plugin_clean.png",u="/document/image/vscode_plugin_clear.png",m="/document/image/vscode_plugin_flash.png",g="/document/image/vscode_plugin_menuconfig.png",h="/document/image/vscode_plugin_monitor.png",f="/document/image/vscode_plugin_serial.png",X=JSON.parse('{"title":"使用 vscode 插件","description":"","frontmatter":{},"headers":[],"relativePath":"zh_CN/get-started/use_vscode_plugin.md","filePath":"zh_CN/get-started/use_vscode_plugin.md","lastUpdated":1744789501000}'),v={name:"zh_CN/get-started/use_vscode_plugin.md"};function x(b,e,k,q,P,F){return i(),a("div",null,e[0]||(e[0]=[t('<h1 id="使用-vscode-插件" tabindex="-1">使用 vscode 插件 <a class="header-anchor" href="#使用-vscode-插件" aria-label="Permalink to &quot;使用 vscode 插件&quot;">​</a></h1><div class="note custom-block github-alert"><p class="custom-block-title">作者</p><p>kirto</p></div><p>本文主要介绍如何使用 vscode 插件简化开发流程</p><h1 id="安装-xfusion-vscode-插件" tabindex="-1">安装 XFusion vscode 插件 <a class="header-anchor" href="#安装-xfusion-vscode-插件" aria-label="Permalink to &quot;安装 XFusion vscode 插件&quot;">​</a></h1><p>在 vscode 的拓展商店搜索 xfusion</p><p><img src="'+n+'" alt="vscode_plugin"></p><p>点击安装即可安装插件</p><p><img src="'+c+'" alt="vscode_plugin_logo"></p><p>安装完插件左侧边栏会出现一个xfusion图标。点击图标会出现下面的启动页面。</p><p><img src="'+s+'" alt="vscode_plugin_start"></p><p>当然，也可以在插件设置中配置路径。 点击<kbd>选择路径</kbd>，找到 XFusion 路径后点击 <code>以后不再提示</code> 然后关闭这个页面。 就完成了 vscode 插件安装</p><h1 id="如何使用-xfusion-插件" tabindex="-1">如何使用 XFusion 插件 <a class="header-anchor" href="#如何使用-xfusion-插件" aria-label="Permalink to &quot;如何使用 XFusion 插件&quot;">​</a></h1><div class="warning custom-block github-alert"><p class="custom-block-title">⚠️注意</p><p>执行以下操作前至少要打开一个终端 插件的本质是可视化的调用指令</p></div><h2 id="export-的过程" tabindex="-1">export 的过程 <a class="header-anchor" href="#export-的过程" aria-label="Permalink to &quot;export 的过程&quot;">​</a></h2><p>点击右下角的 xfusion 标签</p><p><img src="'+p+'" alt="vscode_plugin_export"></p><p>选择一个 target 。这里对应着命令行中的<code>激活 xfusion</code>操作。</p><p><img src="'+l+'" alt="vscode_plugin_target"></p><p>此时，插件就会自动发送 export 命令到终端了。</p><h2 id="插件编译" tabindex="-1">插件编译 <a class="header-anchor" href="#插件编译" aria-label="Permalink to &quot;插件编译&quot;">​</a></h2><p>点击左侧边栏的 logo。点击<code>编译</code>图标。这一步对应命令行的操作为<a href="./xf_command_reference.html#build">xf build</a>。</p><p><img src="'+r+'" alt="vscode_plugin_build"></p><p>这时会出现文件目录树。目录树主要展示你调用组件目录和源文件。</p><p><img src="'+d+'" alt="vscode_plugin_file_tree"></p><p>目录树会有以下几种目录：</p><ul><li>public_components (公共组件库)</li><li>user_components (用户组件库)</li><li>user_dirs (用户文件夹)</li><li>user_main (用户主文件夹)</li></ul><h2 id="插件清除" tabindex="-1">插件清除 <a class="header-anchor" href="#插件清除" aria-label="Permalink to &quot;插件清除&quot;">​</a></h2><p>点击<code>清除</code>图标。这一步对应命令行的操作为<a href="./xf_command_reference.html#clean">xf clean</a>。</p><p><img src="'+_+'" alt="vscode_plugin_clean"></p><p>则清除编译产生的中间产物，并清空目录树的显示</p><p><img src="'+u+'" alt="vscode_plugin_clear"></p><h2 id="插件烧录" tabindex="-1">插件烧录 <a class="header-anchor" href="#插件烧录" aria-label="Permalink to &quot;插件烧录&quot;">​</a></h2><p>点击<code>下载</code>图标。这一步对应命令行的操作为<a href="./xf_command_reference.html#flash">xf flash</a>。</p><p><img src="'+m+'" alt="vscode_plugin_flash"></p><h2 id="插件打开-menuconfig" tabindex="-1">插件打开 menuconfig <a class="header-anchor" href="#插件打开-menuconfig" aria-label="Permalink to &quot;插件打开 menuconfig&quot;">​</a></h2><p>点击<code>菜单</code>图标。这一步对应命令行的操作为<a href="./xf_command_reference.html#menuconfig">xf menuconfig</a>。 <img src="'+g+'" alt="vscode_plugin_menuconfig"></p><h2 id="插件打开串口监视器" tabindex="-1">插件打开串口监视器 <a class="header-anchor" href="#插件打开串口监视器" aria-label="Permalink to &quot;插件打开串口监视器&quot;">​</a></h2><p>点击<code>监视器</code>图标。这一步对应命令行的操作为<a href="./xf_command_reference.html#monitor">xf monitor [串口号]</a>。</p><p><img src="'+h+'" alt="vscode_plugin_monitor"></p><p>然后，选择串口。就可以在命令行中查看了。</p><p><img src="'+f+'" alt="vscode_plugin_serial"></p>',41)]))}const $=o(v,[["render",x]]);export{X as __pageData,$ as default};
